  transition-property: all;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-duration: 150ms;
}
.transition-colors {
  transition-property: color, background-color, border-color, text-decoration-color, fill, stroke;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-duration: 150ms;
}
.transition-opacity {
  transition-property: opacity;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-duration: 150ms;
}
.transition-transform {
  transition-property: transform;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-duration: 150ms;
}
.duration-300 {
  transition-duration: 300ms;
}
.duration-500 {
  transition-duration: 500ms;
}
.duration-700 {
  transition-duration: 700ms;
}

:root {
  /* Ekaant Brand Colors */
  --ekaant-green: #166534;
  --ekaant-light-green: #22c55e;
  --ekaant-dark-green: #14532d;
  --ekaant-accent: #ffd700;

  /* Extended Color Palette */
  --forest-green: #0f5132;
  --earth-brown: #8b4513;
  --sky-blue: #87ceeb;
  --sunset-orange: #ff6b35;

  /* Responsive Typography Scaling */
  --font-size-xs: clamp(0.7rem, 0.65rem + 0.25vw, 0.875rem);
  --font-size-sm: clamp(0.8rem, 0.75rem + 0.25vw, 1rem);
  --font-size-base: clamp(0.9rem, 0.85rem + 0.3vw, 1.125rem);
  --font-size-lg: clamp(1rem, 0.95rem + 0.35vw, 1.25rem);
  --font-size-xl: clamp(1.1rem, 1rem + 0.4vw, 1.5rem);
  --font-size-2xl: clamp(1.25rem, 1.1rem + 0.75vw, 2rem);
  --font-size-3xl: clamp(1.5rem, 1.25rem + 1.25vw, 2.5rem);
  --font-size-4xl: clamp(1.75rem, 1.5rem + 1.5vw, 3rem);
  --font-size-5xl: clamp(2rem, 1.75rem + 2vw, 4rem);
  --font-size-6xl: clamp(2.5rem, 2rem + 2.5vw, 5rem);

  /* Responsive Spacing System */
  --spacing-xs: clamp(0.25rem, 0.2rem + 0.25vw, 0.5rem);
  --spacing-sm: clamp(0.5rem, 0.4rem + 0.5vw, 1rem);
  --spacing-md: clamp(0.75rem, 0.6rem + 0.75vw, 1.5rem);
  --spacing-lg: clamp(1rem, 0.8rem + 1vw, 2rem);
  --spacing-xl: clamp(1.5rem, 1.2rem + 1.5vw, 2.5rem);
  --spacing-2xl: clamp(2rem, 1.5rem + 2.5vw, 3.5rem);
  --spacing-3xl: clamp(2.5rem, 2rem + 2.5vw, 4rem);
  --spacing-4xl: clamp(3rem, 2.5rem + 3vw, 5rem);
  --spacing-5xl: clamp(4rem, 3rem + 4vw, 6rem);

  /* Component-specific fluid dimensions */
  --hero-height: clamp(60vh, 70vh, 100vh);
  --card-height: clamp(200px, 15vw + 150px, 300px);
  --button-padding-x: clamp(1rem, 2vw + 0.5rem, 2.5rem);
  --button-padding-y: clamp(0.5rem, 1vw + 0.25rem, 1rem);
  --border-radius-sm: clamp(0.25rem, 0.5vw, 0.5rem);
  --border-radius-md: clamp(0.5rem, 1vw, 1rem);
  --border-radius-lg: clamp(0.75rem, 1.5vw, 1.5rem);
  --border-radius-xl: clamp(1rem, 2vw, 2rem);
  --border-radius-full: clamp(1.5rem, 3vw, 3rem);

  /* Container Widths */
  --container-sm: min(90vw, 640px);
  --container-md: min(90vw, 768px);
  --container-lg: min(90vw, 1024px);
  --container-xl: min(90vw, 1280px);
  --container-2xl: min(90vw, 1536px);
}

html {
  scroll-behavior: smooth;
}

body {
  font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
  line-height: 1.6;
  color: #374151;
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f1f5f9;
}

::-webkit-scrollbar-thumb {
  background: #166534;
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: #14532d;
}

/* Smooth transitions for all elements */
* {
  transition: color 0.2s ease, background-color 0.2s ease, border-color 0.2s ease, opacity 0.2s ease, transform 0.2s ease;
}

/* Typography improvements */
h1, h2, h3, h4, h5, h6 {
  font-weight: 700;
  letter-spacing: -0.025em;
  line-height: 1.2;
}

p {
  margin-bottom: 1rem;
}

/* Link styling */
a {
  color: var(--ekaant-green);
  text-decoration: none;
  transition: all 0.2s ease;
}

a:hover {
  color: var(--ekaant-dark-green);
  text-decoration: underline;
}

/* Button improvements */
button {
  cursor: pointer;
  transition: all 0.3s ease;
}

button:focus {
  outline: 2px solid var(--ekaant-light-green);
  outline-offset: 2px;
}

/* Form inputs */
input[type="email"], 
input[type="text"], 
textarea {
  transition: all 0.2s ease;
}

input[type="email"]:focus, 
input[type="text"]:focus, 
textarea:focus {
  outline: 2px solid var(--ekaant-light-green);
  outline-offset: 2px;
}

/* Animation utilities */
@keyframes fadeIn {
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
}

@keyframes slideIn {
  from { transform: translateX(-20px); opacity: 0; }
  to { transform: translateX(0); opacity: 1; }
}

.animate-fade-in {
  animation: fadeIn 0.6s ease-out;
}

.animate-slide-in {
  animation: slideIn 0.5s ease-out;
}

/* Responsive utility classes */
.responsive-text-xs { font-size: var(--font-size-xs); }
.responsive-text-sm { font-size: var(--font-size-sm); }
.responsive-text-base { font-size: var(--font-size-base); }
.responsive-text-lg { font-size: var(--font-size-lg); }
.responsive-text-xl { font-size: var(--font-size-xl); }
.responsive-text-2xl { font-size: var(--font-size-2xl); }
.responsive-text-3xl { font-size: var(--font-size-3xl); }
.responsive-text-4xl { font-size: var(--font-size-4xl); }
.responsive-text-5xl { font-size: var(--font-size-5xl); }
.responsive-text-6xl { font-size: var(--font-size-6xl); }

.responsive-spacing-xs { padding: var(--spacing-xs); }
.responsive-spacing-sm { padding: var(--spacing-sm); }
.responsive-spacing-md { padding: var(--spacing-md); }
.responsive-spacing-lg { padding: var(--spacing-lg); }
.responsive-spacing-xl { padding: var(--spacing-xl); }
.responsive-spacing-2xl { padding: var(--spacing-2xl); }
.responsive-spacing-3xl { padding: var(--spacing-3xl); }

.responsive-container { 
  width: var(--container-xl);
  margin: 0 auto;
}

/* Responsive breakpoint-specific adjustments */
@media (max-width: 480px) {
  :root {
    --spacing-2xl: clamp(2rem, 1.5rem + 2.5vw, 4rem);
    --spacing-3xl: clamp(2.5rem, 2rem + 2.5vw, 5rem);
  }
}

@media (min-width: 481px) and (max-width: 768px) {
  :root {
    --container-xl: min(95vw, 1280px);
  }
}

@media (min-width: 769px) and (max-width: 1024px) {
  :root {
    --container-xl: min(92vw, 1280px);
  }
}

@media (min-width: 1025px) {
  :root {
    --container-xl: min(90vw, 1280px);
  }
}

/* Print styles */
@media print {
  * {
    color-adjust: exact;
    -webkit-print-color-adjust: exact;
  }
}

/* Accessibility improvements */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }

  html {
    scroll-behavior: auto;
  }
}
